  beforeMount(){
 ScrollTrigger.refresh()
  },
  mounted() {

    gsap.to('.three-d',{opacity:1,delay:0.9,duration:0.8});

    gsap.to('.outline',{opacity:0,delay:2});

    gsap.fromTo(".arrow", { y: 0,opacity:0.8 }, { y: 10 ,opacity:1,ease:'power2.in',duration:0.4,yoyo:true,repeat:-1});

    gsap.fromTo("h1", { y: 40,opacity:0 }, { y: 0 ,opacity:1,ease:'power3.out',duration:1,delay: 1});

    gsap.from('.intro-content',{y: 100,opacity:0,duration:0.8,ease:'power2.out', scrollTrigger: {
          trigger: '.intro-content',
          start: "top bottom",
          // end: "+=500",
          // markers: true,
        },})
    gsap.fromTo(
      ".hero_popz_img",
      { x: -100, scale: 0.9, opacity: 0 },
      { x: 50, scale: 1, opacity: 1, duration:1, delay: 1,ease:'power3.out' }
    );
    gsap.fromTo(
      ".hero_rock_img",
      { x: 100, scale: 0.9, opacity: 0 },
      { x: -50, scale: 1, opacity: 1, duration: 1, delay: 1,ease:'power3.out' }
    );
    //TEXTS
    var scrText = gsap.utils.toArray(".scr-text");

    scrText.forEach((text) => {
      gsap.from(text, {
        y: 200,
        opacity: 0,
        duration:1.2,
        ease: "power2.out",
        scrollTrigger: {
          trigger: text,
          start: "center bottom",
          // end: "+=500",
          // markers: true,
        },
      });
    });
    //IMAGES
    var images = gsap.utils.toArray(".bike-img");

     images.forEach((img) => {
      gsap.from(img, {
        y: 80,
        opacity: 0,
        duration:0.8,
        ease: "power2.out",
        scrollTrigger: {
          trigger: img,
          start: "top+=50 bottom",
        },
      });
    });
    //DECO ELEMENTS
    var decos = gsap.utils.toArray(".rock-deco-bg")
     decos.forEach((deco) => {
      gsap.from(deco, {
        y: 200,
        opacity: 0,
        scale:0.2,
        duration:2.2,
        ease: "power2.out",
        scrollTrigger: {
          trigger: deco,
          start: "top bottom+=100",
        
          // end: "+=500",
          // markers: true,
        },
      });
    });

  },
};
